import "./ExpenseItem.css";
import "./ExpenseDate.css";

export function ExpenseItem(props) {
  const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];
  const expenseDate = props.expense.date;
  const currMonth = expenseDate.toLocaleString('en-US',{month:'long'})
  const expenseTitle = props.expense.title;
  const price = props.expense.amount;

  return (
    <div className="expense-item">
      <div className="expense-date">
        <div className="expense-date__month">
          {currMonth}
        </div>
        <div className="expense-date__year">{expenseDate.getFullYear()}</div>
        <div className="expense-date__day">{expenseDate.getDay()}</div>
      </div>
      <div className="expense-item__description">
        <h2>{expenseTitle}</h2>
        <div className="expense-item__price">${price.toFixed(2)}</div>
      </div>
    </div>
  );
}
